import React from "react";
import "./TodoItem.css";
import { useState } from "react";

completed = () => {
  setCompleted(true);
  if(completed)
  {
    document.querySelector(".todoItem").style.textDecoration="line-through";
    setCompleted(false);
  }
};
function Todo(props) {
  const [completed,setCompleted] = useState(false);

  const { content } = props;
  return <div className="todoItem" onClick={completed} >{content}</div>;
}

export default Todo;
