import React from "react";
import "./TodoItem.css";
import { useState } from "react";

function Todo(props) {
  const [completed,setCompleted] = useState(false);
  function complete(){
    const handler = document.querySelectorAll(".todoItem");
    handler.forEach(item => {
      item.addEventListener("click",function() {

      })
    })
    setCompleted(true);
    if(completed)
    {
      document.querySelector(".todoItem").style.textDecoration="line-through";
      setCompleted(false);
    }
    else {
      document.querySelector(".todoItem").style.textDecoration="none";
    }
  };
  const { content } = props;
  return <div className="todoItem" onClick={complete} >{content}</div>;
}

export default Todo;
